mapper.insert:
/*j就是插入自己序列号主键的序列号.uId是这个xml里面的实体类的,数据库里面的p_Id. seq_user_info_id及时创建主键序列*/
select seq_productinfo_p_id.nextval from dual
insert into PRODUCTINFO (P_ID, P_CODE, P_NAME, P_TYPE, BRAND, PIC, P_NUM, PRICE, SALE, INTRO, STATUS) values (#{pId,jdbcType=DECIMAL}, #{pCode,jdbcType=DECIMAL}, #{pName,jdbcType=VARCHAR}, #{pType,jdbcType=VARCHAR}, #{brand,jdbcType=VARCHAR}, #{pic,jdbcType=VARCHAR}, #{pNum,jdbcType=DECIMAL}, #{price,jdbcType=DECIMAL}, #{sale,jdbcType=DECIMAL}, #{intro,jdbcType=VARCHAR}, #{status,jdbcType=DECIMAL})
